mapper.select:
SELECT count(DMND_NO) as count
FROM SR_DEMAND
WHERE DMND_NO LIKE #{srCode}

select DMND_NO as dmndNo, rnk as rnk , TTL as ttl, cn as cn, rel_grund as relGrund, rjct_rsn as rjctRsn, SYS_NM as sysNm, SYS_CD as sysCd , clientNm as clientNm, INST_NM as instNm, STTS_NM as sttsNm, STTS_CD as sttsCd, DMND_YMD as dmndYmd, rvwrNm , dept_nm as deptNm, mem.flnm as picNm, CMPTN_DMND_YMD as cmptnDmndYmd, end_ymd as endYmd, task_se_nm as taskSeNm , TASK_SE_CD as taskCd from ( SELECT distinct DMND_NO , rnk , TTL , cn , rel_grund , rjct_rsn , SYS_NM , SYS_CD , clientNm , INST_NM , STTS_NM , STTS_CD , DMND_YMD , c.flnm as rvwrNm , dept_nm , PIC_ID, CMPTN_DMND_YMD , end_ymd , TASK_SE_NM , TASK_SE_CD
from ( SELECT sd.DMND_NO as DMND_NO, si.rnk as rnk , sd.TTL as TTL , sd.cn , sd.rel_grund , sd. rjct_rsn , sys.SYS_NM , sd.SYS_CD , d.dept_nm , si.PIC_ID , m.flnm as clientNm, i.INST_NM as INST_NM, sd.rvwr_id as rvwr_id, s.STTS_NM as STTS_NM, sd.STTS_CD , sd.DMND_YMD as DMND_YMD , sd.CMPTN_DMND_YMD , tk.TASK_SE_NM , sd.TASK_SE_CD , si.end_ymd FROM SR_DEMAND sd, MEMBERS m, INSTITUTION i , STATUS s, SYSTEMS sys, sr_information si, departments d, task tk WHERE sd.DMND_NO = #{dmndNo} and sd.dmnd_no = si.dmnd_no (+) and sd.cust_id = m.member_id and m.inst_cd = i.inst_cd and sd.stts_cd = s.stts_cd and sd.sys_cd = sys.sys_cd and si.dept_cd = d.dept_cd (+) and sd.TASK_SE_CD = tk.TASK_SE_CD
) w , members c where w.rvwr_id = c.member_id(+) )t , members mem where t.PIC_ID = mem.member_id (+)

select SI.sr_no as srNo
from sr_information SI
where SI.dmnd_no=#{dmndNo}

mapper.update:
 UPDATE	SR_DEMAND
	set 

		TTL = #{srRequestDto.ttl} 


		, CN = #{srRequestDto.cn} 


		, REL_GRUND = #{srRequestDto.relGrund} 


		, CMPTN_DMND_YMD = #{srRequestDto.cmptnDmndYmd} 


		, SYS_CD = #{srRequestDto.sysCd} 


		, TASK_SE_CD = #{srRequestDto.taskSeCd} 

	where DMND_NO = #{srRequestDto.dmndNo}

mapper.select:
SELECT SYS_CD
FROM SR_DEMAND
WHERE DMND_NO = #{dmndNo}
